g.script:

var path = document.querySelector('.js-path');
var d = document.querySelector('.js-d');
var coordinate = document.querySelector('.js-coordinate');
var text = document.querySelector('.js-text');
var c1 = document.querySelector('.js-c1');
var ct1 = document.querySelector('.js-ct1');
var cp1 = document.querySelector('.js-cp1');
var desc1 = document.querySelector('.js-desc1');
var c2 = document.querySelector('.js-c2');
var ct2 = document.querySelector('.js-ct2');
var cp2 = document.querySelector('.js-cp2');
var desc2 = document.querySelector('.js-desc2');
var c3 = document.querySelector('.js-c3');
var ct3 = document.querySelector('.js-ct3');
var cp3 = document.querySelector('.js-cp3');
var c4 = document.querySelector('.js-c4');
var ct4 = document.querySelector('.js-ct4');
var cp4 = document.querySelector('.js-cp4');
var desc4 = document.querySelector('.js-desc4');
var isMove1 = false;
var isMove2 = false;
var isMove3 = false;
var isMove4 = false;
c1.addEventListener('mousedown', function(){
  isMove1 = true;
}, false);
c2.addEventListener('mousedown', function(){
  isMove2 = true;
}, false);
c3.addEventListener('mousedown', function(){
  isMove3 = true;
}, false);
c4.addEventListener('mousedown', function(){
  isMove4 = true;
}, false);
coordinate.addEventListener('mousemove', function(event){
  var x = event.clientX - 50;
  var y = event.clientY - 50;
  if(isMove1){
    c1.setAttribute('cx', x);
    c1.setAttribute('cy', y);
    cp1.setAttribute('d', 'M100 200L' + x + ' ' + y);
    ct1.setAttribute('x', x);
    ct1.setAttribute('y', y);
    ct1.innerHTML = '(' + x + ', ' + y + ')';
    path.setAttribute('d', 'M100 200C' + x + ', ' + y + ' ' + c2.getAttribute('cx') + ', ' + c2.getAttribute('cy') + ' 200, 200s'  + (c4.getAttribute('cx') - 200) + ', ' + (c4.getAttribute('cy')-200) +  ' 100, 0');
    d.innerHTML = 'M100 200C' + x + ', ' + y + ' ' + c2.getAttribute('cx') + ', ' + c2.getAttribute('cy') + ' 200, 200s'  + (c4.getAttribute('cx') - 200) + ', ' + (c4.getAttribute('cy')-200) +  ' 100, 0';
  }
  if(isMove2){
    c2.setAttribute('cx', x);
    c2.setAttribute('cy', y);
    cp2.setAttribute('d', 'M200 200L' + x + ' ' + y);
    ct2.setAttribute('x', x);
    ct2.setAttribute('y', y);
    ct2.innerHTML = '(' + x + ', ' + y + ')';
    c3.setAttribute('cx', 400 - x);
    c3.setAttribute('cy', 400 - y);
    cp3.setAttribute('d', 'M200 200L' + (400 - x) + ' ' + (400 - y));
    ct3.setAttribute('x', 400 - x);
    ct3.setAttribute('y', 400 - y);
    ct3.innerHTML = '(' + (400 - x) + ', ' + (400 - y) + ')';

    path.setAttribute('d', 'M100 200C' + c1.getAttribute('cx') + ', ' + c1.getAttribute('cy') + ' ' + x + ', ' + y + ' 200, 200s'  + (c4.getAttribute('cx') - 200) + ', ' + (c4.getAttribute('cy')-200) +  ' 100, 0');
    d.innerHTML = 'M100 200C' + c1.getAttribute('cx') + ', ' + c1.getAttribute('cy') + ' ' + x + ', ' + y + ' 200, 200s'  + (c4.getAttribute('cx') - 200) + ', ' + (c4.getAttribute('cy')-200) +  ' 100, 0';
  }
  if(isMove3){
    c3.setAttribute('cx', x);
    c3.setAttribute('cy', y);
    cp3.setAttribute('d', 'M200 200L' + x + ' ' + y);
    ct3.setAttribute('x', x);
    ct3.setAttribute('y', y);
    ct3.innerHTML = '(' + x + ', ' + y + ')';
    c2.setAttribute('cx', 400 - x);
    c2.setAttribute('cy', 400 - y);
    cp2.setAttribute('d', 'M200 200L' + (400 - x) + ' ' + (400 - y));
    ct2.setAttribute('x', 400 - x);
    ct2.setAttribute('y', 400 - y);
    ct2.innerHTML = '(' + (400 - x) + ', ' + (400 - y) + ')';

    path.setAttribute('d', 'M100 200C' + c1.getAttribute('cx') + ', ' + c1.getAttribute('cy') + ' ' + c2.getAttribute('cx') + ', ' + c2.getAttribute('cy') + ' 200, 200s'  + (c4.getAttribute('cx') - 200) + ', ' + (c4.getAttribute('cy')-200) +  ' 100, 0');
    d.innerHTML = 'M100 200C' + c1.getAttribute('cx') + ', ' + c1.getAttribute('cy') + ' ' + c2.getAttribute('cx') + ', ' + c2.getAttribute('cy') + ' 200, 200s'  + (c4.getAttribute('cx') - 200) + ', ' + (c4.getAttribute('cy')-200) +  ' 100, 0';
  }

  if(isMove4){
    c4.setAttribute('cx', x);
    c4.setAttribute('cy', y);
    cp4.setAttribute('d', 'M300 200L' + x + ' ' + y);
    ct4.setAttribute('x', x);
    ct4.setAttribute('y', y);
    ct4.innerHTML = '(' + x + ', ' + y + ')';
    desc4.innerHTML = '(' + (x-200) + ', ' + (y-200) + ')';
    path.setAttribute('d', 'M100 200C' + c1.getAttribute('cx') + ', ' + c1.getAttribute('cy') + ' ' + c2.getAttribute('cx') + ', ' + c2.getAttribute('cy') + ' 200, 200s'  + (x-200) + ', ' + (y-200) +  ' 100, 0');
    d.innerHTML = 'M100 200C' + c1.getAttribute('cx') + ', ' + c1.getAttribute('cy') + ' ' + c2.getAttribute('cx') + ', ' + c2.getAttribute('cy') + ' 200, 200s'  + (x-200) + ', ' + (y-200) +  ' 100, 0';
    text.innerHTML = 's'  + (x-200) + ', ' + (y-200) + ' 100, 0';
  }
}, false);
c1.addEventListener('mouseup', function(){
  isMove1 = false;
}, false);
c2.addEventListener('mouseup', function(){
  isMove2 = false;
}, false);
c3.addEventListener('mouseup', function(){
  isMove3 = false;
}, false);
c4.addEventListener('mouseup', function(){
  isMove4 = false;
}, false);
